Combinationsconst getCombinations = (arr, selectNumber) =&gt; &#123;  const result = [];  if (selectNumber === 1) return arr.map((value) =&gt; [value]);  arr.forEach((fixed, idx, origin) =&gt; &#123;    const rest = origin.slice(idx + 1);    const combinations = getCombinations(rest, selectNumber - 1);    const attached = combinations.map((element) =&gt; [fixed, ...element]);    result.push(...attached);  &#125;);  return result;&#125;;


